    /**
     * 
     * @param p
     * @return
     */
    @SuppressWarnings("SuspiciousNameCombination")
    public static int getAllowedPlots(Player p) {
        if (p.hasPermission("plots.admin")) {
            return Integer.MAX_VALUE;
        }
        for (int x = 0; x <= 100; x++) {
            if (p.hasPermission("plots.plot." + (100-x))) {
                return 100-x;
            }
        }
        return 0;
    }

